mapper.select:
SELECT id FROM singer WHERE platform

    IS NULL AND name=#{singer.name}
    =#{singer.platform} AND mid=#{singer.mid}

LIMIT 0,1

SELECT id,mid,name,cover,introduce,song_count,album_count,mv_count,fans_count,platform FROM singer WHERE name=#{name} AND platform
IS NULL =#{platform}
LIMIT 0,1

mapper.update:
UPDATE singer
name=#{singer.name}, cover=#{singer.cover}, introduce=#{singer.introduce}, song_count=#{singer.songCount}, album_count=#{singer.albumCount}, mv_count=#{singer.mvCount}, fans_count=#{singer.fansCount}, platform=#{singer.platform}
WHERE id=#{singer.id} OR mid=#{singer.mid}
